import AccordionItem from './AccordionItem';
import { Animated } from 'react-native';
import React, { useRef } from 'react';
import styled from '@emotion/native';
const Container = styled.View `
  flex-direction: column;
  align-items: center;
`;
const Accordion = (props) => {
    const { style, styles, data, collapseOnStart, shouldAnimate, animDuration, activeOpacity, toggleElement, renderTitle, renderBody, } = props;
    const dropDownAnimValueList = useRef(data.map(() => new Animated.Value(0))).current;
    return (<Container style={style}>
      {data.map((datum, titleKey) => {
            return (<AccordionItem key={titleKey} testID={`${titleKey}`} styles={styles} data={datum} collapseOnStart={collapseOnStart} shouldAnimate={shouldAnimate} animDuration={animDuration} activeOpacity={activeOpacity} dropDownAnimValue={dropDownAnimValueList[titleKey]} toggleElement={toggleElement} renderTitle={renderTitle} renderBody={renderBody}/>);
        })}
    </Container>);
};
export default Accordion;
