import React from 'react';
import { StyleSheet, View, TouchableOpacity } from 'react-native';
export function ButtonGroup({ testID, styles, data, renderItem, onPress, touchableOpacityProps, direction = 'row', selectedIndex, borderStyle: { width: borderWidth = 1, color: borderColor, radius: borderRadius = 10, } = {}, }) {
    return (<View testID={testID} style={StyleSheet.flatten([
            { borderRadius, borderColor, borderWidth, overflow: 'hidden' },
            styles?.container,
            { flexDirection: direction },
        ])}>
      {data.map((item, index) => {
            const selected = index === selectedIndex;
            return (<TouchableOpacity disabled={!onPress} {...touchableOpacityProps} onPress={() => onPress && onPress(index, item)} key={index}>
            <View testID={`button-group-item-${index}`} style={StyleSheet.flatten([
                    styles?.button,
                    { borderColor },
                    index !== data.length - 1 &&
                        (direction === 'row'
                            ? { borderRightWidth: borderWidth }
                            : { borderBottomWidth: borderWidth }),
                ])}>
              {renderItem({ item, index, selected })}
            </View>
          </TouchableOpacity>);
        })}
    </View>);
}
ButtonGroup.defaultProps = {
    styles: {
        container: {
            alignSelf: 'center',
        },
    },
};
