import { RadioButton as RadioButtonComp } from './RadioButton';
import React from 'react';
import { View } from 'react-native';
import { Heading3 } from '../Typography/Typography';
import styled from '@emotion/native';
const Container = styled.View `
  flex-direction: column;
  justify-content: center;
`;
const Content = styled.View `
  flex-direction: row;
`;
function RadioGroupContainer(props) {
    const { title, data, selectedValue, selectValue, style, styles, type, labels, labelPosition, } = props;
    return (<Container style={style}>
      <Heading3 style={styles?.title}>{title}</Heading3>
      {title && <View style={{ height: 8 }}/>}
      <Content style={styles?.container}>
        {data.map((datum, i) => {
            return (<RadioButtonComp key={`radio-${i}`} testID={`radio-${i}`} {...props.radioType} label={labels?.[i] || ''} type={type} selected={selectedValue === datum} onPress={() => selectValue?.(datum)} styles={{ label: styles?.label }} labelPosition={labelPosition}/>);
        })}
      </Content>
    </Container>);
}
export const RadioGroup = RadioGroupContainer;
export const RadioButton = RadioButtonComp;
